import {Component, Input, OnInit, SimpleChange} from '@angular/core';
import {RouteParams, Router} from '@angular/router-deprecated';

import {RecipeService} from "./recipe.service";
import {Recipe} from "./recipe";
declare var $:any;

@Component({
    selector: 'recipeNew',
    templateUrl: 'app/recipe-edit.component.html',
    styleUrls: ['app/recipe-edit.component.css']
})
export class RecipeNewComponent {

    private recipe:Recipe;

    placeholderImage = "data:image/jpeg;base64,";

    constructor(private router:Router,
                private recipeService:RecipeService) {

    }

    ngOnInit() {
        this.recipe = new Recipe();
    }

    save() {
        var thiz = this;
        var callback = function(recipeKey) {
            let link = ['RecipeEdit', {key: recipeKey}];
            thiz.router.navigate(link);
        };
        this.recipeService.saveRecipe(this.recipe, callback);
    }
    
    backToRecipes() {
        let link=['Recipes'];
        this.router.navigate(link);
    }

    static goBack() {
        window.history.back();
    }

    ngDoCheck() {
        if (this.recipe.key) {
            console.log("I HAZ ID");
//            let link = ['RecipeEdit', {key: this.recipe.key}];
  //          this.router.navigate(link);
        }
    }

    chooseImg() {
        //noinspection TypeScriptUnresolvedFunction
        $("#imageChooser").trigger("click");
    }

    imgChosen(event) {
        var thiz = this;

        var reader = new FileReader();
        reader.onloadend = function (e:ProgressEvent) {
            var hasResult:FileReader = <FileReader>(e.target);
            //noinspection TypeScriptUnresolvedVariable
            thiz.recipe.image = {"imageData": hasResult.result};
        };
        reader.readAsDataURL(event.target.files[0]);
    }
}
